import React from 'react'
import {Field, reduxForm} from 'redux-form'

class TransactionForm extends React.Component{

    renderInput({input,type,name,lable}){
        return <div>
            <lable>{lable}</lable>
            <input {...input} type={type} name={name}/>
        </div>
    }

    renderSelect(field){
        return <div>
            <label>{field.lable}</label>
            <select {...field.input} name={field.name}>
                {field.children}
            </select>
        </div>
    }

    render(){
        return <div>
            <Field name="amount" lable="Amount" component={this.renderInput} type="number"/>
            <Field name="currency" lable="Currency" component={this.renderSelect} >
                <option value="NA">-select-</option>
                {
                this.props.currencies != null  && this.props.currencies.map((currency)=>{
                    return <option value={currency}>{currency}</option>
                })
                }
            </Field>
            <Field name="type" lable="TransactionType" component={this.renderSelect}>
                    <option value="NA">-select-</option>
                    <option value="debit">debit</option>
                    <option value="credit">credit</option>
            </Field>
            <Field name="amount" lable="Amount" component={this.renderInput} type="number"/>
        </div>
    }
}

export default reduxForm({
    form : 'Transaction'
})(TransactionForm)
