import React from 'react'
import {connect} from 'react-redux'
import { findOpponent, pollOpponent } from '../actions/useraction'
import histroy from '../history'

class PVPHome extends React.Component{

    componentDidMount(){
        this.props.findOpponent();
        this.props.pollOpponent();
    }

    componentDidUpdate(prevProps){
        if(this.props.pvp.state == "SEARCHING"){
            this.props.pollOpponent();
        }
    }

    render(){
        return <div>
            <button onClick={()=>{histroy.push("/home")}}>back</button>
            {this.props.pvp && this.props.pvp.status}
            {this.props.pvp && this.props.pvp.opponent}
        </div>
    }
}

const mapStateToProps = (state) => {
    return {
        pvp : state.pvp
    }
}

export default connect(mapStateToProps,{findOpponent,pollOpponent})(PVPHome);
