import React from 'react'
import {Field, reduxForm} from 'redux-form'

class TransactionForm extends React.Component{

    renderInput({input,type,name,lable}){
        return <div>
            <lable>{lable}</lable>
            <input {...input} type={type} name={name}/>
        </div>
    }

    renderSelect({select, name, lable}){
        return <div>
            <label>{lable}</label>
            <select {...select} name={name}></select>
        </div>
    }

    render(){
        return <div>
            <Field name="amount" lable="Amount" component={this.renderInput} type="number"/>
            <Field name="currency" lable="Currency" component={this.renderSelect} >
                <option >111</option>
                <option >111</option>
                <option >1</option>
            </Field>
            <Field name="amount" lable="Amount" component={this.renderInput} type="number"/>
        </div>
    }
}

export default reduxForm({
    form : 'Transaction'
})(TransactionForm)
