export const saveUser = (username, token) => {
    return {
        type : "SAVE_USER",
        payload : {
            username : username,
            token : token
        }
    }
}

export const loadLeaderboards = () => {
    return {
        type : "LOAD_LEADERBOARDS",
        payload : {}
    }
}

export const loadLeaderboardData = (leaderboard) => {
    return {
        type : "LOAD_LEADERBOARD_DATA",
        payload : {
            leaderboard : leaderboard
        }
    }
}
