import React from 'react'
import {Field, reduxForm} from 'redux-form'

class TransactionForm extends React.Component{

    renderInput({input,type,name,lable,meta}){
        return <div>
            <label>{lable}</label>
            {/* {( meta.touched && meta.error )? <div style={{color:"red"}}>{meta.error}</div> : "" } */}
            <input {...input} type={type} name={name}/>
        </div>
    }

    renderSelect(field){
        return <div>
            <label>{field.lable}</label>
            <select {...field.input} name={field.name}>
                {field.children}
            </select>

        </div>
    }

    transact = (formValues) => {
        console.log(formValues)
        this.props.transaction(formValues.type,formValues.currency,formValues.amount);
    }

    render(){
        return <div>
            <form onSubmit={this.props.handleSubmit(this.transact)}>
            <Field name="amount" lable="Amount" component={this.renderInput} type="number"></Field>
            <Field name="currency" lable="Currency" component={this.renderSelect} >
                <option value="NA">-select-</option>
                {
                this.props.currencies != null  && this.props.currencies.map((currency)=>{
                    return <option value={currency}>{currency}</option>
                })
                }
            </Field>
            <Field name="type" lable="TransactionType" component={this.renderSelect}>
                    <option value="NA">-select-</option>
                    <option value="debit">debit</option>
                    <option value="credit">credit</option>
            </Field>
            <Field component={(field) => { return ( field.meta.touched && field.meta.error )? <div style={{color:"red"}}>{field.meta.error}</div> : "" }}/>
            <button type="submit">Submit</button>
            </form>
        </div>
    }
}

const validate = (formValues) => {
    const errors = {}

    if(!formValues.currency){
        errors.currency = "Select currency"
    }

    if(!formValues.type){
        errors.currency = "Select type"
    }

    if(!formValues.amount || formValues.amount < 0){
        errors.currency = "Enter amount greater than 0"
    }
    return errors
}

export default reduxForm({
    form : 'Transaction',
    validate
})(TransactionForm)
