import React from 'react'
import {connect} from 'react-redux'
import {updatePvpScore, endGame, updateScoresInState} from '../actions/useraction'
import histroy from '../history'
import {Button, Table, Row, Col, Card, Container} from 'react-bootstrap'
import Cell from './cell'

class PVPTikTokToeGame extends React.Component{

    state = {
        seconds : 60,
        selectedData : {
            1 : {
                selected : true,
                player : "Nithin1"
            },
            2 : {
                selected : true,
                player : "Nithin1"
            },
            4 : {
                selected : true,
                player : "Nithin1"
            },
            5 : {
                selected : true,
                player : "Nithin1"
            },
            6 : {
                selected : false,
                player : ""
            },
            7 : {
                selected : true,
                player : "Nithin1"
            },
            8 : {
                selected : true,
                player : "Nithin1"
            },
            9 : {
                selected : true,
                player : "Nithin1"
            }
        }}



    componentDidMount(){

        this.ws = new WebSocket('ws://localhost:8081/ws');
        this.ws.onopen = () => this.ws.send(JSON.stringify({
            gameId : this.props.pvp.gameId,
            isJoin : "true",
            Player : this.props.username
        }));

        this.ws.addEventListener("message", this.updateCache)

        // setInterval(
        //     ()=>{ this.setState({
        //         seconds : this.state.seconds - 1
        //     })},1000
        // )
        // setTimeout(
        //     this.endGameHandler, 60000);
    }

    updateCache = (e) => {
        var msg = JSON.parse(e.data);
        console.log("Updated",msg)
        this.props.updateScoresInState(msg)
    }

    endGameHandler = () =>{
        this.props.endGame()
    }

    submitHandler = () => {
        let random = (Math.round(Math.random()) * 10) + (Math.round(Math.random()) * 20)
        this.props.updatePvpScore(random);
    }

    renderGameGrid = () => {

        return <div>
            <Container>
            <Row>
                <Col></Col>
                {[1,2,3].map((cellId) =>{
                return <Cell style={{width:"5%"}} id={cellId} selected={this.state.selectedData[cellId]} selectCell={this.selectCell}/>
                })}
                <Col></Col>
            </Row>
            <Row>
                <Col></Col>
                {[4,5,6].map(id =>{
                    return <Cell style={{width:"5%"}}  id={id} selected={this.state.selectedData[id]} selectCell={this.selectCell}/>
                })}
                <Col></Col>
            </Row>
            <Row>
                <Col></Col>
                {[7,8,9].map(id =>{
                    return <Cell style={{width:"5%"}}  id={id} selected={this.state.selectedData[id]} selectCell={this.selectCell}/>
                })}
                <Col></Col>
            </Row>
            </Container>

        </div>



    }

    selectCell = (id) =>{

    }

    render = () => {
        return <div>
            <Row style={{paddingBottom:"1%"}}>
                <Col>
                    <Button style={{backgroundColor:"#24bfb8",border:"#24bfb8"}} onClick={()=>{histroy.push("/home")}}>back</Button>
                </Col>
            </Row>
            <Card>
            <Card.Header>
            TikTokToe
            </Card.Header>
            <Card.Body>
                {this.renderGameGrid()}
            </Card.Body>
            </Card>
        </div>
    }

}

const mapStateToProps = (state) => {
    return {
        username : state.user.username,
        pvp : state.pvp,
        pvpScores : state.pvpScores
    }
}

export default connect(mapStateToProps,{updatePvpScore,endGame,updateScoresInState})(PVPTikTokToeGame);
