import React from 'react'
import gameserver from '../apis/gameserver'
import { saveUser } from '../actions/useraction'
import { connect } from 'react-redux'
import { Link } from 'react-router-dom'

class Login extends React.Component{

    user = { username :"", password : ""}

    constructor(){
        super();

    }


    login = async (event) => {
        event.preventDefault()
        let response = await gameserver.post("/login",{
                playerName:this.user.username,
	            password:this.user.password
        })

        console.log(response)
        if(response != null & response.data.token){
            this.props.saveUser("Nithin1", "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VybmFtZSI6Ik5pdGhpbjIiLCJleHAiOjE1ODY1MDA1NjZ9.kI4b2iHUyKicYldOKNTUqGsQR9AT0NB0r2IOwDZBpt0")
        }else{
            alert("Technical Error!")
        }


    }

    changeHandler = (event) => {
        event.preventDefault()
        this.user[event.target.name] = event.target.value

    }
    render(){
        return <div style={{"margin-left":"10%"}}>
            <h2>Login</h2>
            <form onSubmit={this.login}>
                <label>UserName</label>
                <input type={"Text"} name="username" onChange={this.changeHandler}/><br></br>
                <label>Password</label>
                <input type="password" name="password" onChange={this.changeHandler}/><br></br>
                <Link  to="/Home" onClick={this.login} >Login</Link><br></br>
            </form>
        </div>

    }

}

const mapStateToProps = (state) => {
    return state;
}

export default connect(mapStateToProps,{saveUser})(Login);
