import React from 'react'
import {connect} from 'react-redux'
import { findOpponent, pollOpponent } from '../actions/useraction'
import histroy from '../history'
import {Spinner, Button} from 'react-bootstrap'

class PVPHome extends React.Component{

    componentDidMount(){
        this.props.findOpponent();
    }

    componentDidUpdate(){
        if(this.props.pvp.status != "FOUND"){
            console.log("pollOpponent")
            this.props.pollOpponent();
        }else{
            console.log("pollOpponent",this.props.pvp.status)
            histroy.push("/game/"+this.props.pvp.gameId)
        }
    }

    render(){
        return <div>
            <Row>
                <Col>
                    <Button onClick={()=>{histroy.push("/home")}}>back</Button>
                </Col>
            </Row>
            <Row>
                <Col>
                    <h2>{this.props.pvp && this.props.pvp.status} </h2>
                    <Spinner size="xm" animation="border" />
                {/* <Button onClick={()=>{histroy.push("/game/"+this.props.pvp.gameId)}}>Start Game</Button> */}
                </Col>
            </Row>
            </div>

    }
}

const mapStateToProps = (state) => {
    return {
        pvp : state.pvp
    }
}

export default connect(mapStateToProps,{findOpponent,pollOpponent})(PVPHome);
