import React from 'react'
import {Field, reduxForm} from 'redux-form'

class LoginFormComponent extends React.Component{

    renderInput({input, lable, meta}){
        //return <input onChange={formProps.input.onChange} value={formProps.input.value} type="text"/>
        console.log(meta)
        return (
            <div>
                <label>{lable}</label>
                <input {...input} autoComplete="off"/>
                {
                     meta.touched && meta.error ? <div style={{color:"red"}}>{meta.error}</div> : ""
                }
            </div>

        )
    }

    onSubmit(formValues){
        this.props.onLoginClick(formValues.username, formValues.password)
    }

    render(){
        return <div>
            <form onSubmit={this.props.handleSubmit(this.onSubmit)}>
            <Field name="username" component={this.renderInput} lable="Username"/>
            <Field name="password" component={this.renderInput} lable="Password"/>
            <button type="submit">Submit</button>
            </form>

        </div>
    }
}

const validate = formValues =>{
    const errors={}
    if(!formValues.username){
        errors.username = "Enter username" 
    }
    if(!formValues.password){
        errors.password = "Enter password" 
    }
    return errors;
}

export default reduxForm({
    form : 'FormCreate',
    validate
})(LoginFormComponent);
