import React from 'react'


class GameConatiner extends React.Component{

    state={ leaderboards:["Test","Test1"], score:0}

    getLeaderboarOptions = () => {

    }

    changeHandler = (event) => {
        let key = event.target.name
        let val = event.target.value
        this.setState({
            [key] : val
        })
    }

    submitHandler = () => {
        let random = (Math.abs(Math.random()) * 10) + (Math.abs(Math.random()) * 20)
        this.setState({
            score :  random
        })
    }

    render(){
        return <div>
            <form onSubmit={this.submitHandler}>
                <select onChange={this.changeHandler}>
                    {
                        this.state.leaderboards.map((lb) => {
                            return <option key={lb}>{lb}</option>
                        })
                    }
                </select><br></br>
                <button type="submit">Press to update score</button>
            </form>
            {this.state.score}
        </div>
    }
}

export default GameConatiner
