import React from 'react'
import gameserver from '../apis/gameserver'
import { saveUser } from '../actions/useraction'
import { connect } from 'react-redux'

class Login extends React.Component{

    state = { status :""}

    login = async (event) => {
        event.preventDefault()
        let response = await gameserver.post("/login",{
                playerName:this.state.username,
	            password:this.state.password
        })

        console.log(response)
        if(response != null){
            this.setState({
                status : response.data.status
            })
        }else{

            this.setState({
                status : "Technical Error!"
            })
        }

    }

    changeHandler = (event) => {
        event.preventDefault()
        let ename = event.target.name
        let value = event.target.value
        this.setState({
            [ename] : value
        })
    }
    render(){
        return <div style={{"margin-left":"10%"}}>
            <h2>Login</h2>
            <form onSubmit={this.login}>
                <label>UserName</label>
                <input type={"Text"} name="username" onChange={this.changeHandler}/><br></br>
                <label>Password</label>
                <input type="password" name="password" onChange={this.changeHandler}/><br></br>
                <button type="submit">Login</button><br></br>
            </form>
            {this.state.status}
        </div>

    }

}

export default connect()(Login);
