import React from 'react'


class GameConatiner extends React.Component{

    state={ leaderboards:["Test","Test1"]}

    getLeaderboarOptions = () => {

    }

    changeHandler = (event) => {
        let key = event.target.name
        let val = event.target.value
        this.setState({
            [key] : val
        })
    }

    render(){
        return <div>
            <form onSubmit={this.submitHandler}>
                <select onChange={this.changeHandler}>
                    {
                        this.state.leaderboards.map((lb) => {
                            return <option>{lb}</option>
                        })
                    }
                </select>
                <button type="submit">Press to update score</button>
            </form>
        </div>
    }
}

export default GameConatiner
