import React from 'react'
import gameserver from '../apis/gameserver'
import { saveUser } from '../actions/useraction'
import { connect } from 'react-redux'

class Login extends React.Component{

    constructor(){
        super();
        let user = { username :"", password : ""}
    }


    login = async (event) => {
        event.preventDefault()
        // let response = await gameserver.post("/login",{
        //         playerName:this.user.username,
	    //         password:this.user.password
        // })

        //console.log(response)
        // if(response != null){
        //     this.setState({
        //         status : response.data.status
        //     })
        // }else{

        //     this.setState({
        //         status : "Technical Error!"
        //     })
        // }

        this.props.saveUser(this.user.username, this.user.password)
    }

    changeHandler = (event) => {
        event.preventDefault()
        this.user.username = event.target.name
        this.user.password = event.target.value

    }
    render(){
        return <div style={{"margin-left":"10%"}}>
            <h2>Login</h2>
            <form onSubmit={this.login}>
                <label>UserName</label>
                <input type={"Text"} name="username" onChange={this.changeHandler}/><br></br>
                <label>Password</label>
                <input type="password" name="password" onChange={this.changeHandler}/><br></br>
                <button type="submit">Login</button><br></br>
            </form>
            {this.state.status}
        </div>

    }

}

const mapStateToProps = (state) => {
    return state;
}

export default connect(mapStateToProps,{saveUser})(Login);
