import React from 'react'
import {connect} from 'react-redux'
import Home from './home'
import Index from './index'
import Modal from './modal'
import {Row,Col,Navbar} from 'react-bootstrap'

class MainComponent extends React.Component{

    state = {
        message : {},
        othersMessage : []
    }

    componentDidMount(){
        if(this.props.match.params){
            console.log(this.props.match.params)
            this.ws = new WebSocket('ws://localhost:8081/chat/'+this.props.match.params.from);

        this.ws.addEventListener("message",(mes)=>{
            console.log("res",mes)
            this.setState({
                othersMessage : [...this.state.othersMessage, JSON.parse(mes.data)]
            })
        })
        }
    }

    sendMessage = () =>{
        this.ws.send(JSON.stringify(this.state.message));
        this.setState({
            othersMessage : [...this.state.othersMessage, this.state.message]
        })
    }

    onChangeHandler = (event) => {
        console.log(event.target.value)
        this.setState({
            message : {
                sender : this.props.match.params.from,
                recipient : this.props.match.params.to,
                content : event.target.value
            }
        })

    }

    renderComponent = () => {
        return <div>
            <Navbar bg="dark" variant="dark">
            {this.props.match.params.to}
            </Navbar>
            <Navbar fixed="bottom">
            <input type="text" name="message" onChange={this.onChangeHandler}/>
            <button onClick={this.sendMessage}>Send Message</button>

            </Navbar>
            </div>

    }

    render(){
        return (
            <div>
                {/* <Modal /> */}
                {this.renderComponent()}
            </div>
        )
    }
}

const mapStateFromProps = (state) => {
    return {
        username : state.user.username
    }
}

export default connect(mapStateFromProps)(MainComponent)
