import React from 'react'
import {connect} from 'react-redux'
import { updateScore, loadLeaderboards, loadLeaderboardData} from '../actions/useraction'

class GameConatiner extends React.Component{

    player = {leaderboardId :"", score:""}

    componentDidMount(){
        this.props.loadLeaderboards();
    }

    changeHandler = (event) => {
        event.preventDefault()
        let key = event.target.name
        let val = event.target.value
        console.log("changeHandler",key,val)
        this.player[key] = val
    }

    submitHandler = async (event) => {
        event.preventDefault()
        let random = (Math.round(Math.random()) * 10) + (Math.round(Math.random()) * 20)
        this.player.score = random;
        console.log(this.player)
        this.props.updateScore(this.player.leaderboardId,this.props.username,random)
        //this.props.loadLeaderboardData(this.player.leaderboardId)
    }

    render(){
        return <div>
            <form onSubmit={this.submitHandler}>
                <select name="leaderboardId" onChange={this.changeHandler}>
                    {
                        this.props.leaderboards != null && this.props.leaderboards.map((lb) => {
                            return <option key={lb}>{lb}</option>
                        })
                    }
                </select><br></br>
                <button type="submit">Press to update score</button>
            </form>
            Your current game score : {this.player.score}
        </div>
    }
}

const mapStateToProps = (state) => {
    return {
        error : state.errorMessage,
        username : state.user.username,
        leaderboards : state.leaderboards
    }
}

export default connect(mapStateToProps,{updateScore,loadLeaderboards,loadLeaderboardData})(GameConatiner)
