import React from 'react'
import gameserver from '../apis/gameserver'
import { saveUser } from '../actions/useraction'
import { connect } from 'react-redux'
import { Redirect } from 'react-router-dom'
import GoogleAuth from './googleAuth'
import LoginFormComponent from './loginFormComponent'

class Login extends React.Component{

    user = { username :"", password : ""}

    constructor(props) {
        super(props);
      }

    login = async (username,password) => {
        let response = await gameserver.post("/login",{
                playerName:username,
	            password:password
        })

        console.log(response)
        if(response != null & response.data.token != null){
            this.props.saveUser(password, response.data.token)
        }else{
            alert("Technical Error!")
        }


    }

    render(){
        return <div style={{"margin-left":"10%"}}>
            <h2>Login</h2>
            <LoginFormComponent onLoginClick={this.login}/>
            <GoogleAuth />
        </div>

    }

}

const mapStateToProps = (state) => {
    return state;
}

export default connect(mapStateToProps,{saveUser})(Login);
