import React from 'react'
import {connect} from 'react-redux'
import { loadBalances } from '../actions/useraction'


class DisplayWallet extends React.Component{


    componentDidMount(){
        this.props.loadBalances(this.props.username)
    }

    render(){
        return <div>
            {console.log(this.props)}
            {

                this.props.balances != null && this.props.balances.map((balance) =>{

                    return <div> {balance} </div>
                })
            }
        </div>
    }
}

const mapStateToProps = (state) => {
    return {
        balances : state.balances,
        username : state.user.username
    }
}

export default connect(mapStateToProps,{loadBalances})(DisplayWallet)
