import React from 'react'
import LeaderboardView from './leaderboard'
import {connect} from 'react-redux'

class LeaderboardDashboard extends React.Component{

    state = { leaderboards :["Test","Test1"] }

    render(){
        return <div>
            <h1>Leaderboard Dashboard</h1>
            <div className={"leaderboards-list"}>
            {
                this.state.leaderboards.map((lbname)=>{
                    return <LeaderboardView leaderboardName={lbname}/>
                })
            }
            </div>


        </div>
    }
}

const mapStateToProps = (state) => {
    return {
        leaderboards : state.leaderboards
    }
}

export default connect(mapStateToProps)(LeaderboardDashboard)
