import React from 'react'
import {Field, reducer, reduxForm} from 'redux-form'

class FormComponent extends React.Component{

    renderInput({input, lable, meta}){
        //return <input onChange={formProps.input.onChange} value={formProps.input.value} type="text"/>
        console.log(meta)
        return (
            <div>
                <label>{lable}</label>
                <input {...input}/>
                <div>{meta.error}</div>
            </div>

        )
    }

    onSubmit(formValues){
        console.log(formValues)
    }

    render(){
        return <div>
            <form onSubmit={this.props.handleSubmit(this.onSubmit)}>
            <Field name="username" component={this.renderInput} lable="username"/>
            <Field name="password" component={this.renderInput} lable="password"/>
            <button type="submit">Submit</button>
            </form>

        </div>
    }
}

const validate = formValues =>{

    const errors={}

    console.log(formValues)
    if( !formValues.username ){
        return errors.username = "Enter username"

    }
    return errors;
}

export default reduxForm({
    form : 'FormCreate',
    validate
})(FormComponent);
