import React from 'react'
import {connect} from 'react-redux'
import {updatePvpScore, endGame} from '../actions/useraction'
import histroy from '../history'
import {Button, Jumbotron, Row, Col, Card} from 'react-bootstrap'

class PVPGame extends React.Component{

    state = {seconds : 20}

    componentDidMount(){
        setInterval(
            ()=>{ this.setState({
                seconds : this.state.seconds - 1
            })},1000
        )
        setTimeout(
            this.endGameHandler, 20000);
    }

    endGameHandler = () =>{
        this.props.endGame()
    }

    submitHandler = () => {
        let random = (Math.round(Math.random()) * 10) + (Math.round(Math.random()) * 20)
        this.props.updatePvpScore(random);
    }


    renderGame = () => {
        return <div>
            <Row style={{paddingBottom:"1%"}}>
                <Col>
                    <Button onClick={()=>{histroy.push("/home")}}>back</Button>
                </Col>
            </Row>
            <Card>
            <Card.Header>
            <h4>Game Id : {this.props.pvp.gameId}</h4>
                        <h4>Time : {this.state.seconds}</h4>
            </Card.Header>
            <Card.Body>
            <Row>
                <Col style={{paddingLeft:"10%"}}>
                <Card style={{ width: '23rem'}}>
                    <Card.Img variant="top" src="/images/user.png" />
                    <Card.Body>
                        <Card.Title>{this.props.username}</Card.Title>
                        <Card.Text>Score : {this.props.pvpScores[this.props.username]}</Card.Text>
                        <Button variant="primary" onClick={this.submitHandler} >Play</Button>
                    </Card.Body>
                </Card>
                </Col>
                <Col>
                <Card style={{ width: '23rem'}}>
                    <Card.Img variant="top" src="/images/user.png"/>
                    <Card.Body>
                        <Card.Title>{this.props.pvp.opponent}</Card.Title>
                        <Card.Text>Score : {this.props.pvpScores[this.props.pvp.opponent]}</Card.Text>
                        {/* <Button variant="primary" onClick={this.submitHandler} >Play</Button> */}
                    </Card.Body>
                </Card>
                </Col>
            </Row>
            </Card.Body>
    </Card>


    </div>
    }

    renderWinner = () => {
        return <div>
            <Button onClick={()=>{histroy.push("/home")}}>back</Button>
            <Jumbotron>
            <h2>Game Finished</h2>
            <h2>Winner is {this.props.pvp.winner[this.props.pvp.opponent] ? this.props.pvp.opponent : this.props.username} 
    [score  : {this.props.pvp.winner[this.props.pvp.opponent] ? this.props.pvp.winner[this.props.pvp.opponent] : this.props.pvp.winner[this.props.username]}]</h2>
            </Jumbotron>
        </div>
    }

    render(){
        return <div>
            {this.props.pvp && this.props.pvp.gameStatus != "Finished" ? this.renderGame() : this.renderWinner()}
        </div>
    }
}

const mapStateToProps = (state) => {
    return {
        username : state.user.username,
        pvp : state.pvp,
        pvpScores : state.pvpScores
    }
}

export default connect(mapStateToProps,{updatePvpScore,endGame})(PVPGame);
