import React from 'react'
import {connect} from 'react-redux'
import {loadCurrencies} from '../actions/useraction'
import gameserver from '../apis/gameserver'

class Transaction extends React.Component{

    trans = { amount : 0, currency :"", type :""};

    componentDidMount(){
        this.props.loadCurrencies()
    }



    handleChange = (event) => {
        event.preventDefault()
        console.log(event.target.name)
        console.log(event.target.value)
        this.trans[event.target.name] = event.target.value;
    }

    handleSubmit = async () => {
        const response =  await gameserver.post("/wallet/"+this.trans.type,{
            playerName:"Nithin2",
            amount: this.trans.amount,
            currency: this.trans.currency
        })

        this.setState({
            status : response.data.status
        })
    }

    render(){
        return <div>
            <h2>Transaction Type : {this.props.name}</h2>
            <form>
                <label >Amount </label> 
                <input type="number" name="amount" onChange={this.handleChange} /><br></br>
                <label >Currency </label> 
                <select name="currency" onChange={this.handleChange}>
                {this.props.currencies != null  && this.props.currencies.map((currency)=>{
                    return <option value={currency}>{currency}</option>
                })}

                </select><br></br>
                <label >Transaction Type </label> 
                <select name="type" onChange={this.handleChange}>
                    <option value="debit">debit</option>
                    <option value="credit">credit</option>
                </select><br></br>
                <button type="button" onClick={this.handleSubmit}>submit</button>
            </form>
            {this.state.status}
        </div>
    }
}

const mapStateToProps = (state) => {
    return {
        currencies : state.currencies,
        user : state.user
    }
}

export default connect(mapStateToProps,{loadCurrencies})(Transaction)
