            noOfSubLeaderboards : response.data.subLeaderboards.length
        })
        const arr = []
        var i =0;
        response.data.subLeaderboards.map((lb)=>{
            arr[i++] = response.data.details.lb
        })
        console.log(arr)
    }

    render(){
        return(
            <div>
