import React from 'react'
import gameserver from '../apis/gameserver'
import {saveUser} from '../actions/useraction'
import {connect} from 'react-redux'
import RegisterFormComponent from './registerFormComponent'

class Register extends React.Component{

    register = async (username,password,email,mobileNo) =>{
        let response = await gameserver.post("/register",{
                playerName:username,
                    password:password,
                    email:email,
                    mobileNo:mobileNo

        })

        console.log(response)
        if(response != null & response.data.token){
            this.props.saveUser(this.user.username, response.data.token)
        }else{
            alert("Technical Error!")
        }
    }

    render(){
        return <div style={{"margin-left":"10%"}}>
            <h2>Register</h2>
            <RegisterFormComponent />
        </div>
    }
}

const mapStateToProps = (state) =>{
    return state;
}

export default connect(mapStateToProps,{saveUser})(Register)
