import React from 'react'
import {connect} from 'react-redux'
import { findOpponent } from '../actions/useraction'

class PVPHome extends React.Component{

    componentDidMount(){
        this.props.findOpponent();
    }

    render(){
        return <div>
            {this.props.pvp && this.props.pvp.status}
        </div>
    }
}

const mapStateToProps = (state) => {
    return {
        pvp : state.pvp
    }
}

export default connect(mapStateToProps,{findOpponent})(PVPHome);
