import React from 'react'

class Register extends React.Component{

    state = {}

    changeHandler = (event) => {
        kname = event.target.name
        kvalue = event.target.value

        if(kname == "re-password" && value != this.state.password){
            console.log("password mis match")
        }

        this.setState({
            [kname]:kvalue
        })
    }

    submitHandler = () =>{
        console.log(this.state)
    }

    render(){
        return <div>
            <form onSubmit={this.submitHandler}>
            <label>Username</label>
            <input type="text" name="username" onChange={this.changeHandler} />
            <label>Password</label>
            <input type="password" name="password" onChange={this.changeHandler} />
            <label>Re-enter Password</label>
            <input type="password" name="re-password" onChange={this.changeHandler} />
            <label>Mobile No</label>
            <input type="number" name="mobileNo" onChange={this.changeHandler} />
            <label>Email Id</label>
            <input type="email" name="email" onChange={this.changeHandler} />

            <button type="submit" >Register</button>
            </form>

        </div>
    }
}

export default Register
