import {combineReducers} from 'redux'


const usersReducer = (oldUser = { username :"Nithin3", token:"" }, action) => {
    if(action.type == "SAVE_USER"){
        return {
            username : action.payload.username,
            token: action.payload.token
        }
    }

    return oldUser
}

const leaderboardsReducers = (leaderboards=[], action) => {
    if(action.type == "LOAD_LEADERBOARDS"){
        return{
            leaderboards : action.payload.leaderboards
        }
    }

    return leaderboards;
}

const leaderboardsDataReducers = (leaderboardDetails ={}, action) => {
    if(action.type == "LOAD_LEADERBOARDS_DATA"){
        let leaderboardDetailsTemp = leaderboardDetails
        leaderboardDetailsTemp[action.payload.leaderboardId] = action.payload.leaderboardsData
        console.log("reducer",leaderboardDetailsTemp[action.payload.leaderboardId])
        return leaderboardDetailsTemp
    }
    return leaderboardDetails;
}

export default combineReducers({
    user: usersReducer,
    leaderboards : leaderboardsReducers,
    leaderboardsData : leaderboardsDataReducers
})
