import React from 'react'
import {connect} from 'react-redux'
import {updatePvpScore, endGame} from '../actions/useraction'

class PVPGame extends React.Component{

    componentDidMount(){
        setTimeout(
            function() {
                this.props.endGame()
            }, 10000);
    }

    submitHandler = () => {
        let random = (Math.round(Math.random()) * 10) + (Math.round(Math.random()) * 20)
        this.props.updatePvpScore(random);
    }


    renderGame = () => {
        return <div>
        <h2>Game Id : {this.props.pvp.gameId}</h2>

        <h3>Scores : </h3>
        <table>
            <tr>
                <td>{this.props.username}</td>
                <td>{this.props.pvp.opponent}</td>
            </tr>
            <tr>
                <td>{this.props.pvpScores[this.props.username]}</td>
                <td>{this.props.pvpScores[this.props.pvp.opponent]}</td>
            </tr>
        </table>

        <button type="submit" onClick={this.submitHandler} >Click here for score</button>
    </div>
    }

    render(){
        return <div>
            {this.props.pvp && this.props.pvp.gameStatus != "Finished" ? this.renderGame() : "Game Finished"}
        </div>
    }
}

const mapStateToProps = (state) => {
    return {
        username : state.user.username,
        pvp : state.pvp,
        pvpScores : state.pvpScores
    }
}

export default connect(mapStateToProps,{updatePvpScore,endGame})(PVPGame);
