import React from 'react'
import {connect} from 'react-redux'
import {updatePvpScore, endGame, updateScoresInState} from '../actions/useraction'
import histroy from '../history'
import {Button, Table, Row, Col, Card, Container} from 'react-bootstrap'
import Cell from './cell'
import gameserver from '../apis/gameserver'

class PVPTikTokToeGame extends React.Component{

    state = {
        seconds : 60,
        selectedData : {},
        myTurn : true
    }



    componentDidMount(){

        this.ws = new WebSocket('ws://localhost:8081/ws');
        this.ws.onopen = () => this.ws.send(JSON.stringify({
            gameId : "TikTokToe",
            isJoin : "true",
            Player : this.props.username
        }));

        this.ws.addEventListener("message", this.updateCache)
    }

    updateCache = (e) => {
        var msg = JSON.parse(e.data);
        console.log("Received ",msg)
        this.updateGridVals(msg)
        this.setState({
            myTurn : true
        })
    }

    updateGridVals = (msg) => {
        var updatedValues = {...this.state.selectedData}
        updatedValues[msg.id] = msg.player
        this.setState({
            selectedData : updatedValues
        })

    }

    endGameHandler = () =>{
        this.props.endGame()
    }

    submitHandler = () => {
        let random = (Math.round(Math.random()) * 10) + (Math.round(Math.random()) * 20)
        this.props.updatePvpScore(random);
    }

    selectCellHandler = async (id) =>{
        if(this.state.myTurn){
            console.log("Selected : ",id)
            const response = await gameserver.post("/matchmaking/tiktoktoe",{
                player : this.props.username,
                id:id
            })

            if(response){
                this.updateGridVals({
                    player : this.props.username,
                    id : id
                })
            }

            this.setState({
                myTurn : false
            })
        }else{
            alert("Wait for your turn")
        }
    }

    renderGameGrid = () => {

        return <div>
            <Container>
            <Row>
                <Col></Col>
                <Col></Col>
                <Col></Col>
                {[1,2,3].map((id) =>{
                return <Cell key={id} style={{width:"5%"}} id={id} selected={this.state.selectedData[id]} selectCell={this.selectCellHandler}/>
                })}
                <Col></Col>
                <Col></Col>
                <Col></Col>
            </Row>
            <Row>
                <Col></Col>
                <Col></Col>
                <Col></Col>
                {[4,5,6].map(id =>{
                    return <Cell key={id} style={{width:"5%"}}  id={id} selected={this.state.selectedData[id]} selectCell={this.selectCellHandler}/>
                })}
                <Col></Col>
                <Col></Col>
                <Col></Col>
            </Row>
            <Row>
                <Col></Col>
                <Col></Col>
                <Col></Col>
                {[7,8,9].map(id =>{
                    return <Cell key={id} style={{width:"5%"}}  id={id} selected={this.state.selectedData[id]} selectCell={this.selectCellHandler}/>
                })}
                <Col></Col>
                <Col></Col>
                <Col></Col>
            </Row>
            </Container>
        </div>
    }


    render = () => {
        return <div>
            <Row style={{paddingBottom:"1%"}}>
                <Col>
                    <Button style={{backgroundColor:"#24bfb8",border:"#24bfb8"}} onClick={()=>{histroy.push("/home")}}>back</Button>
                </Col>
            </Row>
            <Card>
            <Card.Header>
            <h2>TikTokToe {this.props.pvp.gameId}</h2>
            <h3>{this.state.myTurn ? "Your Turn":"Opponent Turn"}</h3>
            </Card.Header>
            <Card.Body>
                {this.renderGameGrid()}
            </Card.Body>
            </Card>
        </div>
    }

}

const mapStateToProps = (state) => {
    return {
        username : state.user.username,
        pvp : state.pvp
    }
}

export default connect(mapStateToProps,{updatePvpScore,endGame,updateScoresInState})(PVPTikTokToeGame);
