import React from 'react'
import {connect} from 'react-redux'
import {updatePvpScore} from '../actions/useraction'

class PVPGame extends React.Component{

    submitHandler = () => {
        let random = (Math.round(Math.random()) * 10) + (Math.round(Math.random()) * 20)
        this.props.updatePvpScore(random);
    }

    render(){
        return <div>
            <h2>Game Id : {this.props.pvp.gameId}</h2>

            <h3>Scores : </h3>
            <table>
                <tr>
                    <td>{this.props.username}</td>
                    <td>{this.props.pvp.opponent}</td>
                </tr>
                <tr>
                    <td></td>
                </tr>
            </table>

            <button type="submit" onClick={this.submitHandler} >Click here for score</button>
        </div>
    }
}

const mapStateToProps = (state) => {
    return {
        username : state.user.username,
        pvp : state.pvp,
        pvpScores : state.pvpScores
    }
}

export default connect(mapStateToProps,{updatePvpScore})(PVPGame);
