import React from 'react'
import gameserver from '../apis/gameserver'

class Login extends React.Component{

    state = { }

    login = () => {
        let response = gameserver.post("/login",{
            params:{
                playerName:this.state.username,
	            password:this.state.password
            }
        })
    }

    changeHandler = (event) => {
        let ename = event.target.name
        let value = event.target.value
        this.setState({
            [ename] : value
        })
    }
    render(){
        return <div>
            <h2>Login</h2>
            <form onSubmit={this.login}>
                <label>UserName</label>
                <input type={"Text"} name="username" onChange={this.changeHandler}/><br></br>
                <label>Password</label>
                <input type="password" name="password" onChange={this.changeHandler}/><br></br>
                <button type="submit">Login</button><br></br>
            </form>
        </div>

    }

}

export default Login;
