import React from 'react'
import gameserver from '../apis/gameserver.js'
import LeaderboardTable from './leaderboardTable.js'

class LeaderboardView extends React.Component{

    state = { leaderboardEntries :[] , playerScores :{}}

    constructor (){
        super();

        console.log(this.props)
        this.loadLeaderboard();
    }

    componentDidMount(){
    }

    componentWillUpdate(){
        this.loadLeaderboard();
    }

     loadLeaderboard = async () => {
        const response =  await gameserver.get('/leaderboard/'+this.props.leaderboardName)
        const arr = []
        var i =0;
        response.data.subLeaderboards.map((lb)=>{
            arr[i++] = response.data.players[lb]
        })
        this.setState({
            leaderboardEntries : arr,
            playerScores : response.data.details
        })
        console.log(arr)
    }

    render(){
        var i =0;
        return(
            <div className={"leaderboard"}>
                <h2>Leaderboard Name : {this.props.leaderboardName}</h2>
                { 
                this.state.leaderboardEntries.map((lb)=>{
                    return <LeaderboardTable key={i++} instance={i++} players={lb} playerScores={this.state.playerScores}/>
                  })
                }
            </div>
        )
    }
}

export default LeaderboardView;
