import React from 'react'
import gameserver from '../apis/gameserver.js'
import LeaderboardTable from './leaderboardTable.js'

class LeaderboardView extends React.Component{

    state = { leaderboardEntries :[] }

    constructor(){
        super()
        const response = gameserver.get('/leaderboard/Test')
        console.log(response)

    }

     loadLeaderboard = async () => {
        const response =  await gameserver.get('/leaderboard/Test')
        const arr = []
        var i =0;
        response.data.subLeaderboards.map((lb)=>{
            arr[i++] = response.data.players[lb]
        })
        this.setState({
            leaderboardEntries : arr
        })
        console.log(arr)
    }

    getLeaderboards(){
        loadLeaderboard()
        var i =0;
        this.state.leaderboardEntries.map((lb)=>{
           return <LeaderboardTable instance={i++} players={lb}/>
         })
    }

    render(){
        return(
            <div>
                <button onClick={this.loadLeaderboard}>LOAD LEADERBOARDS</button>
                {this.getLeaderboards()}
            </div>
        )
    }
}

export default LeaderboardView;
