import React from 'react'
import {connect} from 'react-redux'
import { loadLeaderboardData } from '../actions/useraction'
import LeaderboardTable from './leaderboardTable';


class LeaderboardView extends React.Component{

    componentDidMount(){
        console.log("componentDidMount LeaderboardView")
        this.props.loadLeaderboardData(this.props.leaderboardName);

    }

    render(){
        var i =0;
        return(
            <div className={"leaderboard"}>

                <h2>Leaderboard1 : {this.props.leaderboardName}</h2>
                <div id={this.props.leaderboardName}>
            {
                this.props.leaderboardData.leaderboardDetails != null && 

                <LeaderboardTable players={this.props.leaderboardData.leaderboardDetails.subLeaderboards}/>
            } 
                </div>

            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        leaderboardData : state.leaderboardsData
    }
}
export default connect(mapStateToProps,{loadLeaderboardData})(LeaderboardView);
