import React from 'react'
import {connect} from 'react-redux'
import {loadCurrencies} from '../actions/useraction'
import gameserver from '../apis/gameserver'

class Transaction extends React.Component{

    trans = { amount : 0, currency :""};

    state = {
        status : ""
    }

    componentDidMount(){
        this.props.loadCurrencies()
    }



    handleChange = (event) => {
        event.preventDefault()
        this.trans[event.target.name] = event.target.value;
    }

    handleSubmit = async () => {
        const response =  await gameserver.post("/wallet/credit",{
            playerName:"Nithin2",
            amount: this.trans.amount,
            currency: this.trans.currency
        })

        this.setState({
            status : response.data.status
        })
    }

    render(){
        return <div>
            <h2>Transaction Type : {this.props.name}</h2>
            <form>
                <label >Amount </label> 
                <input type="number" name="amount" /><br></br>
                <label >Currency </label> 
                <select name="currency">
                {this.props.currencies != null  && this.props.currencies.map((currency)=>{
                    return <option>{currency}</option>
                })}

                </select><br></br>
                <button type="submit" onClick={this.handleSubmit}>submit</button>
            </form>
            {this.state.status}
        </div>
    }
}

const mapStateToProps = (state) => {
    return {
        currencies : state.currencies,
        user : state.user
    }
}

export default connect(mapStateToProps,{loadCurrencies})(Transaction)
