import React from 'react'
import gameserver from '../apis/gameserver'
import {saveUser} from '../actions/useraction'
import {connect} from 'react-redux'

class Register extends React.Component{

    state = { statu : "", country:"India"}

    changeHandler = (event) => {
        event.preventDefault()
        let kname = event.target.name
        let kvalue = event.target.value

        if(kname == "re-password" && kvalue != this.state.password){
            console.log("password mis match")
        }

        this.setState({
            [kname]:kvalue
        })
    }

    submitHandler = async (event) =>{
        event.preventDefault()
        let response = await gameserver.post("/register",{
                playerName:this.state.username,
                    password:this.state.password,
                    email:this.state.email,
                    mobileNo:this.state.mobileNo

        })

        console.log(response)
        if( response != null){
            this.setState({
                status : response.data.status
            })
        }else{
            this.setState({
                status : "Technical Error!"
            })
        }

        this.props.save
    }

    render(){
        return <div style={{"margin-left":"10%"}}>
            <h2>Register</h2>
            <form onSubmit={this.submitHandler}>
            <label>Username</label>
            <input type="text" name="username" onChange={this.changeHandler} /><br></br>
            <label>Password</label>
            <input type="password" name="password" onChange={this.changeHandler} /><br></br>
            <label>Re-enter Password</label>
            <input type="password" name="re-password" onChange={this.changeHandler} /><br></br>
            <label>Mobile No</label>
            <input type="number" name="mobileNo" onChange={this.changeHandler} /><br></br>
            <label>Email Id</label>
            <input type="email" name="email" onChange={this.changeHandler} /><br></br>
            <label>Country</label>
            <select name="country" onChange={this.changeHandler}>
                <option value="India">India</option>
                <option value="Australia">Australia</option>
                <option value="Canada">Canada</option>
                <option value="Srilanka">Srilanka</option>
                <option value="Others">Others</option>
            </select><br></br>
            <button type="submit" >Register</button><br></br>
            </form>
        </div>
    }
}

export default Register
