import {combineReducers} from 'redux'


const usersReducer = (oldUser = { username :"", token:"" }, action) => {
    if(action.type == "SAVE_USER"){
        return {
            username : action.payload.username,
            token: action.payload.token
        }
    }

    return oldUser
}

export default combineReducers({
    user: usersReducer
})
